import React, { useEffect } from 'react'
import BannerTop from '../components/BannerTop'
import SidebarRight from '../components/SidebarRight'
import SidebarMain from '../components/SidebarMain'
import ArticlesContext from "../components/context/articlesContext";
import { getArticles, overrideCache } from '../lib/api'
import { API_URL } from '../lib/constants';


const indexPage = ({ articles, isServerRendered }) => {

    useEffect(() => {
        // When the page is server-rendered,
        // we override the value in the client cache
        if (isServerRendered) {
            console.log('page dentro de effect')
            overrideCache(API_URL, articles);
          }
    }, [])
   
    return (
        <main>

            <BannerTop />

            <div className="lay-sidebar">
            
                <ArticlesContext.Provider value={{ articles }}>
                    <SidebarMain />
                </ArticlesContext.Provider>

                <SidebarRight />

            </div>
        </main>
    )
}

//function que es llamada en cada request de la página. Solo será renderizada cuando esten los datos (mejor para el SEO)
export async function getStaticProps(context ) {
    const articles = await getArticles();
    const isServerRendered = !!context.req;
    console.log('paso por get static prop ' + isServerRendered)
    //const articles = await getArticles();
    // Le envio a la página los articulos via props
    return { props: { articles, isServerRendered } }
}

export default indexPage
