import React, {useState} from 'react';
import Title from './Title'
import TagsMenu from './TagsMenu'
import ListArticle from './ListArticle';


const SidebarMain = () => {
    const [expanded, setExpanded] = useState(false)

    return (
        <div className="sidebar__main">

            <Title>Acumulado Grilla</Title>

            <TagsMenu />

            <ListArticle expanded={expanded} />

            {expanded ? (
                <section className="row">
                    <div className="col-12 hlp-text-center hlp-margintop-40">
                        <button onClick={() => setExpanded(false)} className="--btn --secondary">MENOS NOTAS DE ACUMULADO GRILLA</button>
                    </div>
                </section>
            ) : (
                    <section className="row">
                        <div className="col-12 hlp-text-center hlp-margintop-40">
                            <button onClick={() => setExpanded(true)} className="--btn --secondary">M√ÅS NOTAS DE ACUMULADO GRILLA</button>
                        </div>
                    </section>
                )
            }
        </div>
    )
};



export default SidebarMain;
