import React from 'react'
import {slugs} from '../test/dummy-slugs'
import lscache from 'lscache';
import { API_URL } from './constants';
//import fetch from 'isomorphic-fetch';

const TTL_MINUTES = 1;

async function fetchAPI(url, options) {

  // We don't cache anything when server-side rendering.
  // That way if users refresh the page they always get fresh data.
  if (typeof window === 'undefined') {
      const jsonUncache = await fetch(url, options).then(res => res.json());
      if (json.errors) {
        console.error(json.errors)
        throw new Error('Error en traer datos de la API')
      }

      return jsonUncache;
  }

  let cachedResponse = lscache.get(url);

  // If there is no cached response,
  // do the actual call and store the response
  if (cachedResponse === null) {
    cachedResponse = await fetch(url, options)
      .then(res => res.json());
    lscache.set(url, cachedResponse, TTL_MINUTES);

    if (cachedResponse.errors) {
      console.error(cachedResponse.errors)
      throw new Error('Error en traer datos de la API')
    }
  }

  return cachedResponse;

  /*const res = await fetch(url, options)

  const json = await res.json()
  if (json.errors) {
    console.error(json.errors)
    throw new Error('Error en traer datos de la API')
  }

  return json;*/
}

export async function getArticles() {
  const options = {
                    method: 'GET',
                    headers: {
                      'Content-Type': 'application/json'
                    }
                  };

  const data = await fetchAPI(API_URL, options);
  //Si no llegara a encontrarse articulos, paso un array vacio para que el resto de la p치gina se renderize
  return data.articles ? data.articles : [];
}

export function overrideCache(key, val) {
  lscache.set(key, val, TTL_MINUTES);
}

export function getSlugs() {
  const data = slugs;
  //Si no llegara a encontrarse articulos, paso un array vacio para que el resto de la p치gina se renderize
  return data;
}
