import React, { useContext, useState } from 'react';
import Article from './Article'
import ArticlesContext from '../components/context/articlesContext'

const ListArticle = () => {

    const context = useContext(ArticlesContext)
    const [expanded, setExpanded] = useState(false)

    const articlesFiltered = context.articles.filter((art => art.subtype === "7"))

    const renderArticles = articles => {
        const articlesToShow = expanded ? articles : articles.slice(0, 8);
        return articlesToShow.map(article => {
            return <Article key={article._id} article={article} />
        })
    }

    return (
        <section className="row-gap-tablet-2 row-gap-deskxl-3 hlp-degrade">
            {articlesFiltered.length === 0 && <p className="row">No se han encontrado articulos</p>}
            {renderArticles(articlesFiltered)}
            <div className="transparency" ></div>

            {expanded ? (
                <section className="row">
                    <div className="col-12 hlp-text-center hlp-margintop-40">
                        <button onClick={() => setExpanded(false)} className="--btn --secondary">MENOS NOTAS DE ACUMULADO GRILLA</button>
                    </div>
                </section>
            ) : (
                    <section className="row">
                        <div className="col-12 hlp-text-center hlp-margintop-40">
                            <button onClick={() => setExpanded(true)} className="--btn --secondary">M√ÅS NOTAS DE ACUMULADO GRILLA</button>
                        </div>
                    </section>
                )
            }

        </section>
    )

};

export default ListArticle;
