export function dateConvert(fecha) {

    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    let dateInJs = new Date(fecha);
    //dateInJs.setDate(dateInJs.getDate() + 1);
    return dateInJs.toLocaleDateString("es-ES", options)
  }
