import React, { useContext } from 'react';
import ArticlesContext from '../components/context/articlesContext'

const TagsMenu = () => {

    const context = useContext(ArticlesContext)
    //console.log(context.articles);
    /*
o	Agrupar, totalizar y ordenar de mayor a menor los tags encontrados en las notas
§	los tags seran encontrados en cada articulo dentro del atributo “taxonomy.tags”
o	Del ordenamiento anterior mostrar los primeros 10 tags debajo del titulo 
o	El href a contener debe ser: “/tema/[tag.slug]”


    */
   
    //Creo un único array con todos los tags de cada artículo
    const singleArray = context.articles.reduce((data, articulo) => {
        //console.log("single", art.taxonomy.tags);
        return data.concat(articulo.taxonomy.tags)
    }, []);

    //Creo un objeto donde cada key-value representa un slug y la cantidad de repeticiones
    const group = singleArray.reduce( (data, tag) => {
        const key = tag[slug];
        if (!data[key]) {
            data[key] = {'cantidad': 1, 'tag':tag};
        } else {
            data[key] = {'cantidad': (data[key].cantidad + 1), 'tag':tag}
        }
        //data[tag.slug] = {'cantidad': (data[tag.slug] || 0) + 1, 'tag':tag};
        //data[tag.slug] = (data[tag.slug] || 0) + 1;
        return data;
      } , {})

    //Obtengo un array con las keys del objeto en base al value de cada una
    const slugSorted = Object.keys(group).sort(function(a,b){return group[b.cantidad]-group[a.cantidad]})

   
    console.log("single", singleArray);
    console.log("group", group);
    console.log("slugSorted", slugSorted);

    return (
        <div className="row">
            <div id="" className="cont_tags com-secondary-tag hlp-marginBottom-20">
                <a href="" className="">Platos principales</a>
                <a href="" className="">Cerdo</a>
                <a href="" className="">Papas</a>
                <a href="" className="">Date un gustito</a>
                <a href="" className="">La familia</a>
            </div>
        </div>
    )
};

export default TagsMenu;
