import React, { useEffect } from 'react'
import Link from 'next/link'
import Router from 'next/router'
import BannerTop from '../components/BannerTop'
import SidebarRight from '../components/SidebarRight'
import SidebarMain from '../components/SidebarMain'
import ArticlesContext  from "../components/context/articlesContext";
import { getArticles } from '../lib/api'


const indexPage = ({ articles }) => {

articles

    return (
        <main>

            <BannerTop />

            <div className="lay-sidebar">

                <ArticlesContext.Provider value={{ articles }}>
                    <SidebarMain />
                </ArticlesContext.Provider>

                <SidebarRight />

            </div>
        </main>
    )
}

// Esta function es llamada en cada request, y la página solo será renderizada cuando esten los datos (mejor para SEO)
export async function getServerSideProps() {
    const articles = await getArticles();
    // Le envio a la página los articulos via props
    return { props: { articles } }
}

export default indexPage
