import React from 'react'

const API_URL = `https://api-test-ln.herokuapp.com/articles`

async function fetchAPI(url, method, data = null) {

  const res = await fetch(url, {
    method: method,
    headers: {
      'Content-Type': 'application/json'
    },
    body: data,
  })

  const json = await res.json()
  if (json.errors) {
    console.error(json.errors)
    throw new Error('Error en traer datos de la API')
  }

  return json;
}

export async function getArticles() {

  let expiry = 1 * 60 // 1 minuto por default
  let cacheKey = API_URL;
  let cached = localStorage.getItem(cacheKey)
  let whenCached = localStorage.getItem(cacheKey + ':ts')

  if (cached !== null && whenCached !== null) {
    let age = (Date.now() - whenCached) / 1000
    if (age < expiry) {
      let response = new Response(cached)
      return Promise.resolve(response)
    }

  } else {
    // We need to clean up this old key
    localStorage.removeItem(cacheKey)
    localStorage.removeItem(cacheKey + ':ts')
  }

  const data = await fetchAPI(API_URL, 'GET');
  //localStorage.setItem(API_URL, data.articles)
  //localStorage.setItem(API_URL + ':ts', Date.now())
  //Si no llegara a encontrarse articulos, paso un array vacio para que el resto de la página se renderize
  return data.articles ? data.articles : [];
}
